def new_floor(grid):
    first = True
    for i in range(len(grid)):
        for j in range(len(grid[i])):
            if grid[i][j] >= 1 and first:
                grid[i][j] = 0
                first = False

            elif grid[i][j] == 0 and not first:
                grid[i][j-1] = 0
                first = True

    return grid


